// 438. Find All Anagrams in a String

// Given a string s and a non-empty string p, find all the start indices of p's anagrams in s.

// Strings consists of lowercase English letters only and the length of both strings s and p will not be larger than 20,100.

// The order of output does not matter.

// Example 1:

// Input:
// s: "cbaebabacd" p: "abc"

// Output:
// [0, 6]

// Explanation:
// The substring with start index = 0 is "cba", which is an anagram of "abc".
// The substring with start index = 6 is "bac", which is an anagram of "abc".
// Example 2:

// Input:
// s: "abab" p: "ab"

// Output:
// [0, 1, 2]

// Explanation:
// The substring with start index = 0 is "ab", which is an anagram of "ab".
// The substring with start index = 1 is "ba", which is an anagram of "ab".
// The substring with start index = 2 is "ab", which is an anagram of "ab".

// Test Cases
// "cbaebabacd"
// "abc"
// "abab"
// "ab"
// "baa"
// "aa"
// "bpaa"
// "aa"
// ""
// ""

#include <map>
#include <vector>
using namespace std;

// Current solution is O(s.size() * #unique Chars in p (max 26)) therefore max time is O(20100*26 = 522,600 )
class Solution {
public:
    bool isAnagram(map<char,int> dest, map<char,int> src ){

        map<char, int>::iterator it;
        for (it = dest.begin(); it != dest.end(); it++){
            if ( it->second != src[it->first]){
                return false;      
            }
        }
        return true;
    }
    vector<int> findAnagrams(string s, string p) {
        vector<int> result;
        if ( s.size() < p.size()) 
            return result;
        
        map<char,int> dest, src;
        for ( int i = 0; i < p.size(); i++ ){
            dest[s[i]]++;
            src[p[i]]++;
        }
        
        if (isAnagram(dest,src)){
            result.push_back(0);
        }
        
        
        for ( int i = 1; i <= s.size()-p.size(); i++ ){
            dest[s[i-1]]--;
            dest[s[i+p.size()-1]]++;
            if (isAnagram(dest,src)){
                result.push_back(i);
            }
        }
        return result;
    }
};

